class Solution {
    public int maxOperations(String s) {
        int count = 0;
        int n = s.length();
        // int i = n-1;
        // while(i>=0 && s.charAt(i) == '1')
        //     i--;
        // for(;i>=0;i--){
        //     if(s.charAt(i) == '1')
        //         count++;
        // }
        int one = 0;
        for(int i=0;i<n;i++){
            if(s.charAt(i) == '1')
                one++;
            else{
                if(i>0 && s.charAt(i-1)=='1'){
                    count+=one;
                }
            }
        }
        return count;


    }
}
