class Solution {
public:
    int maxOperations(string s) {
        int one = 0, res = 0, cur = -1;
        for(int i = 0; i < s.size(); i++){
            if(s[i] != cur){
                if(s[i] == '0') res += one;
                cur = s[i];
            }
            if(s[i] == '1') one++;
        }
        return res;
    }
};
