class Solution {
    int solve(int ans, int count, string& s){
         bool flag=true;
        for(int i=0;i+1<=s.size();i++){
            if(s[i]=='0'){
                if(flag==true) ans+=count;
                flag=false;

            }
            else{
                 flag=true;
                count++;

            }
        }
        return ans;

    }
public:
int maxOperations(string s) {
        int ans=0,count=0;
        return solve(ans, count, s);

    }
};
