class Solution {
public:
    int maxOperations(string s) {
        bool f = false;
        vector<int> v;

        for (char c : s) {
            if (c== '1') {
                v.push_back(1);
                f = true;
            } else if (c == '0' && f) {
                v.push_back(0);
                f = false;
            }
        }
        int ans = 0;
        int p = 0;
        for (int bit : v) {
            if (bit == 1) {
                p++;
            } else {
                ans += p;
            }
        }

        return ans;
    }
};
