class Solution {
public:
    int maxOperations(string s) {
        int cnt_one=0;
        int i=0;
        int cnt=0;
        while(i<s.size()){
            if(s[i]=='1'){
                cnt_one++;
                i++;
            }
            int flag=0;
            while(s[i]=='0'){
                i++;
                flag=1;
            }
            if(flag==1){
                cnt+=cnt_one;
            }
        }
        return cnt;
    }
};
