class Solution {
public int maxOperations(String s) {
        int ans = 0;
        int parity = 0;
        boolean one = false;
        List<Integer> list = new ArrayList<>();

        for (char c : s.toCharArray()) {
            if (c == '1') {
                list.add(1);
                one = true;
            } else if (c == '0' && one) {
                list.add(0);
                one = false;
            }
        }

        for (int l : list) {
            if (l == 1) {
                parity += 1;
            } else {
                ans += parity;
            }
        }

        return ans;
    }
}
