class Solution {
public:
    int maxOperations(string s) {
        int ct=0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1')ct++;
        }
        int ss=0;
        int ans=0;
        bool of=0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                of=1;
                ss++;
            }
            if(s[i]=='0' && of==1){
             ans+=ss;
             of=0;
            }
        }
        return ans;


    }
};
