class Solution:
    def maxOperations(self, s: str) -> int:
        z=0
        count=0
        x=True
        if(s[-1]=='0'):
            x=False
        for i in range(len(s)-1,-1,-1):
            # print(s[i])
            if(s[i]=='1' and x==False):
                z+=1
                # print(z)
                x=True
            elif(s[i]=='0'):
                x=False
            if(x==True):
                count+=z
        return count
