class Solution {
public:
    int maxOperations(string s) {
   int ans=0,ones=0,zero=0;
        bool b=0;
        vector<int>v;
        for(auto it:s){
            if(it=='1'){
                v.push_back(1);
                b=1;
            }
            if(it=='0' && b==1){
                v.push_back(0);
                b=0;
            }
        }
        for(auto it:v){
            if(it==1){
                ones++;
            }
            else {
                ans+=ones;
            }
        }
        return ans;
    }
};
