class Solution {
public:
    int maxOperations(string s) {
        int n=s.length(),ones=0,ans=0;
        for(int i=0;i<n-1;i++){
            if(s[i]=='1'&&s[i+1]=='0'){
                ans++;
                ans+=ones;
            }
            if(s[i]=='1'){
                ones++;
            }

        }
        return ans;
    }
};
