class Solution {
    public int maxOperations(String s) {
        int e = s.length()-1;
        int d = 0;
        int c = 0;
        for(int i=s.length()-2;i>=0;i--){
            if(s.charAt(i)=='1'){
                if(s.charAt(i+1)=='0')
                    c++;
                d+=c;
            }
        }
        // for(int i=s.length()-1;i>=0;i--){
        //     if(s.charAt(i)=='1'){
        //         if(e>i) d++;
        //         e--;
        //     }
        // }
        return d;
    }
}
