inline long long multiply(long long x, long long y) {
	return ((x % MOD) * (y % MOD)) % MOD;
}

inline long long add(long long x, long long y) {
	return ((x % MOD) + (y % MOD))% MOD;
}

class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int c1 = 0;
        char prev = 'z';
        for(char c: s){
            if(c=='0' && prev!='0')
                ans += c1;
            if(c=='1')
                c1++;
            prev = c;
            //deb3(c,c1,prev);
            //deb1(ans);
        }
        return ans;
    }
};
