class Solution:
    def maxOperations(self, s: str) -> int:
        i = 0
        c1 = 0
        res = 0
        while i < len(s):
            if s[i] == '1':
                c1 += 1
                i += 1
            else:
                res += c1
                while i < len(s) and s[i] == '0':
                    i += 1
        return res
