#include <string>
#include <vector>

class Solution {
public:
    int maxOperations(const std::string& s) {
        int count = 0;
        int parity = 0;
        bool found = false;
        vector<int> yy;

        // Iterate through the string
        for (char ch : s) {
            if (ch == '1') {
                yy.push_back(1);
                found = true;
            } else if (ch == '0' && found) {
                yy.push_back(0);
                found = false;
            }
        }

        // Calculate the result
        for (int ch : yy) {
            if (ch == 1) {
                parity++;
            } else {
                count += parity;
            }
        }

        return count;
    }
};
