class Solution {
public:
 int maxOperations(string s) {
       int ans=0;
        int cnt=0;
        bool chk=false;
        // Traverse the string from end to beginning
        for (int i=0;i<s.size();i++) {
            if (s[i] == '0'&&chk==true) {
                ans+=cnt;
                chk=false;
            } 
            else if (s[i] == '1') {
                chk=true;
                cnt++;
            }
        }

        return ans;
    }
};
