class Solution {
public:
    int maxOperations(string s) {
        int current = 0;

        int ops = 0;
        int consecutiveOnes = 0;

        while (current < s.size()) {
            if (s[current] == '0') {
                if (consecutiveOnes > 0) {
                    ops += consecutiveOnes;
                    // set current all the way to next 01
                    while (current + 1 < s.size() and s[current] == '0' and s[current + 1] == '0') ++current;
                }
            } else if (s[current] == '1') {
                ++consecutiveOnes;
            }

            ++current;
        }

        return ops;
    }
};
