class Solution {
    public int maxOperations(String s) {
        int res = 0;
        int cnt = 0; //1
        int n = s.length();
        for(int i = 0; i < n; i++){
            if(s.charAt(i) == '1'){
                cnt++;
                int j = i + 1;
                while (j < n && s.charAt(j) == '0'){
                    j++;
                }
                if(j -  i > 1){
                    res += cnt;
                }
            }
        }
        return res;
    }

}
