class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
         int prev=0;
         int cnt=0,ans=0;
         for(int i=0;i<n;i++)
         {
            if(s[i]=='1')
            {
                int diff=i-prev;
                if(diff>1)
                {
                    ans+=cnt;
                }
                cnt++;
                prev=i;
            }
         }
         if(s[n-1]=='0')ans+=cnt;
         return ans;
    }
};
