class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int pi = -1;
        int cnt = 0;

        s += '1';

        for(int i = 0; i < s.size(); i++) {
            if(s[i] == '1') {
                if(pi + 1 < i) {
                    ans += cnt;
                }
                pi = i;
                cnt++;
            }
        }

        return ans;
    }
};
