class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, zero = 0;
        for(int i =  s.size()-1; i >= 0;  i--){
            if(s[i] == '0'){
                if(i == s.size()-1 || s[i] != s[i+1]) zero++;
            } else {
                ans += zero;
            }
        }
        return ans;
    }
};
