class Solution {
public:
    int maxOperations(string s) {
        long long cnt=0;
        int n=(int)s.size();
        int last=n-1;
        int tot=0;
        for(int i=0;i<n;i++)
        {
            if(s[i]=='1')
            {
                tot++;
            }
        }
        int rem=0;
        for(int i=0;i<n;i++)
        {
            if(s[i]=='1'&&i+1<n&&s[i+1]=='0')
            {
                rem++;
                cnt+=rem;
                tot--;
            }
            else if(s[i]=='1')
            {
                rem++;
            }
        }
        return cnt;
    }
};
