class Solution {
    public int maxOperations(String s) {
        int g=0,ans=0;
        for(int i=s.length()-1;i>=0;i--){
            if(s.charAt(i)=='0') {
                g++;
                while(i>=0 && s.charAt(i)=='0') i--;
            }
            if(i>=0 && s.charAt(i)=='1')ans+=g;
        }
        return ans;
    }
}
