class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(), a = 0, ans = 0;

        for(int i=0;i<n;i++){
            if(s[i] == '0'){
                ans += a;
                while(i<n && s[i] == '0'){
                    i++;
                }
                i--;
            }
            else    a++;
        }

        return ans;

    }
};
