class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int one=0;
        int i=0;


        while(i<s.length()){
            if(s[i]=='1')one++,i++;
            else{
                if(one==0){
                    i++;
                    continue;
                }
                else{
                    ans+=one;
                    while(i<s.length() and s[i]=='0')i++;

                }
            }

        }
        return ans;
    }
};
