class Solution {
public:
    int maxOperations(string s) {
        int cnt=0;
        int res=0;
        for(int i=0;i<s.length();i+=1){
            if(s[i]=='0'){
                while(s[i]=='0'){
                    i+=1;
                }
                i-=1;
                res+=cnt;
            }
            else
                cnt+=1;
        }
        return res;
    }
};
