class Solution {
public:
    int maxOperations(string s) {
        int cnt1=0;
        int ans=0;
        bool flag=false;
        for(int a:s){
            if(a=='1'){
                if(flag){
                    ans+=cnt1;
                }
                cnt1++;
                flag=false;
            }
            else flag=true;
        }
        if(flag) ans+=cnt1;
        return ans;

    }
};
