class Solution:
    def maxOperations(self, s: str) -> int:


        tmp = []
        for i, x in enumerate(s):
            if x == '1':
                tmp.append(i)
        if not tmp:
            return 0

        cnt = len(tmp) - 1
        ans = cnt + 1 if tmp[-1] != len(s) - 1 else 0
        #print(cnt, tmp)
        for i in range(len(tmp) - 1, 0, -1):
            if tmp[i] != tmp[i - 1] + 1:
                ans += cnt
            cnt -= 1
        return ans
