class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int i=s.size()-2;
        int pre=0;
        while(i>=0)
        {
            if(s[i]=='1')
            {
                if(s[i+1]=='0')
                {
                    ans+=pre+1;
                    pre+=1;
                }
                else {
                    ans+=pre;
                }
            }
            i--;

        }
        return ans;
    }
};
