class Solution {
public:
    int maxOperations(string s) {
        int countZero = 0;
        int ans = 0;
        int i=s.size()-1;
        while(i>=0){
            if(s[i]=='0'){
                countZero++;
                i--;
                while(i>=0 && s[i]=='0'){
                    i--;
                }
            }else{
                ans += countZero;
                i--;
            }
        }
        return ans;
    }
};
