class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();

        int ans = 0;
        int cnt = 0;
        int te = 0;
        char prev = '2';
        for(int i=n-1;i>=0;i--){
            if(prev=='2'){
                prev = s[i];
                if(s[i]=='1') cnt++;
            }
            else if(prev=='1' && prev==s[i]) cnt++;
            else if(prev=='1' && prev!=s[i]){
                ans += cnt*te;
                te++;
                cnt = 0;
                prev = '0';
            }
            else{
                prev = s[i];
                if(prev=='1') cnt++;
            }
            if(te==0 && prev=='0') te = 1;
            if(i==0 && cnt>0) ans += cnt*te;
        }

        return ans;
    }
};
