class Solution {
public:
    int maxOperations(string s) {
        vector<int> v1;
    int tp = 0;
    for (auto it : s) {
        if (it == '1') {
            tp++;
        } else {
            if (tp != 0) {
                v1.push_back(tp);
            }
            tp = 0;
        }
    }
    if (tp != 0) {
        v1.push_back(tp);
    }
    long long ans = 0, d = 0;
    for (int it : v1) {
        ans += d;
        d += it;
    }
    if (tp == 0) {
        ans += d;
    }
    return ans;
    }
};
