class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int n=s.size();
        int temp=0;
        for(int i=0;i<n;i++){
            bool z=false;
            if(s[i]=='1'){
                temp++;
                for(i=i+1;i<n;i++){
                    if(s[i]!='1'){z=true;break;}
                    temp++;
                }
            }
            if(z){
                ans+=temp;
            }
        }
        return ans;
    }
};
