class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0;
        long long sum = 0;
        for (int i = (int)s.size() - 2; i >= 0; --i) {
            if (s[i] == '1' && s[i + 1] == '0') {
                ++cnt;
            }

            if (s[i] == '1') {
                sum += cnt;
            }
        }

        return sum;
    }
};
