class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int i=0,n=s.size();
        int cnt=0;
        while(i<n){
            if(s[i]=='0'){
                i++; continue;
            }
            int j=i;
            while(j<n && (s[j]=='1')) j++;
            cnt=cnt+j-i;
            if(j!=n){
                ans+=cnt;
            }
            i=j;
        }
        return ans;
    }
};
