class Solution {
public:
    int maxOperations(string s) {
        int count = 0;
        int n = s.size();
        int ret = 0;

        for (int i = 0; i < n;)
        {
            if (s[i] == '1')
            {
                count++;
                i++;
            }
            else//出现了0，去找结束的0的位置
            {
                int j = i + 1;
                while (j < n && s[j] == '0')
                {
                    j++;
                }
                ret += count;
                i = j;
            }
        }
        return ret;
    }
};
