class Solution {
public:
    int maxOperations(string s) {
        bool ch = true;
        int ans = 0, c = 0;
        for(int i = 0; i < s.size(); i++){
            if(s[i] == '1'){
                c++;
                ch = true;
            }
            else{
                if(ch) {
                    ans += c;
                } 
                ch = false;
            }
        }
        return ans;
    }
};
