class Solution {
public:
    int maxOperations(string t) {
        ios_base::sync_with_stdio(0);
        cin.tie(0);

        int res = 0;
        int p = 0;
        bool hasOne = false;
        vector<int> bits;

        for (char ch : t) {
            if (ch == '1') {
                bits.push_back(1);
                hasOne = true;
            } else if (ch == '0' && hasOne) {
                bits.push_back(0);
                hasOne = false;
            }
        }

        for (int bit : bits) {
            if (bit == 1) {
                p += 1;
            } else {
                res += p;
            }
        }

        return res;
    }
};
