class Solution {
public:
    int maxOperations(string s) 
    {
        int res = 0;
        int ones = 0;
        vector<int> proc;
        bool zeros = false;


        for (char c : s) 
        {
            if (c == '1') 
            {
                proc.push_back(1);
                zeros = true;
            } else if (c == '0' && zeros) 
            {
                proc.push_back(0);
                zeros = false;
            }
        }

        for (int v : proc) 
        {
            if (v == 1) 
            {
                ones++;
            } else 
            {
                res += ones;
            }
        }

        return res;
    }
};
