class Solution {
public:
    int maxOperations(string s) {
        int value=0,res=0;
        if(s[s.size()-1]=='0')value++;
        for(int i=s.size()-1;i>=0;i--){
            if(s[i]=='1')res+=value;
            else if(i+1<s.size()&&s[i]=='0'&&s[i+1]=='1'){
                value++;
            }
        }
        return res;
    }
};
