class Solution {
public:
    int maxOperations(string s) {

        int running = 0;
        int ans = 0;
        bool chk = true;
        for(char c: s){

            if(c == '1'){
                running++;
                chk = true;
            }
            if(c == '0' && chk){
                ans += running;
                chk = false;
            }

        }

        return ans;
    }
};
