class Solution:
    def maxOperations(self, s: str) -> int:
        # [0, 3, 4, 6]
        #  2  1  1  0
        n = len(s)
        ones = []
        for i in range(n):
            if (s[i] == '1'):
                ones.append(i)
        moves = [0] * len(ones)
        for i in range(len(ones) - 1, -1, -1):
            if (i == len(ones) - 1):
                if (ones[i] < n - 1):
                    moves[i] = 1
            else:
                moves[i] = moves[i + 1]
                if (ones[i] + 1 < ones[i + 1]):
                    moves[i] += 1

        return sum(moves)
