class Solution {
public:
    int maxOperations(string s) {
        int add = 0;
        int sum = 0;
        for(int i = 0;i < s.size();i ++){
            if(s[i] == '1')add ++;
            if(s[i] == '1' && s[i+1] == '0')
            {
                sum += add;
            }
        }
        return sum;
    }

};
