class Solution {
public:
    int maxOperations(string s) {
        vector<int>v;
        int n=s.size();
        int i=0;
        while(i<n)
        {
            while(i<n&&s[i]=='0') i++;
            if(i==n) break;
            int cnt=0;
            while(i<n&&s[i]=='1') i++,cnt++;
            if(i<n) v.push_back(cnt);
        }
        // ranges::sort(v,greater<int>());
        int sum=0,ans=0;
        for(int x:v) sum+=x,ans+=sum;
        return ans;
    }
};
