class Solution {
public:
    int maxOperations(const std::string& s) {
        int operationCount = 0;
        int countOnes = 0;
        bool foundOne = false;
        vector<int> segments;

        for (char ch : s) {
            if (ch == '1') {
                segments.push_back(1);
                foundOne = true;
            } else if (ch == '0' && foundOne) {
                segments.push_back(0);
                foundOne = false;
            }
        }

        for (int segment : segments) {
            if (segment == 1) {
                countOnes++;
            } else {
                operationCount += countOnes;
            }
        }

        return operationCount;
    }
};
