class Solution {
public:
    int maxOperations(string s) {
        int count=0;
        int n=s.length();
        int op=0;
        for(int i=0;i<n-1;i++){
            if(s[i]=='1' && s[i+1]=='0'){
                count++;
                op+=count;
            }
            else if(s[i]=='1' && s[i+1]=='1')count++;
        }
        return op;
    }
};
