class Solution {
public:
    int maxOperations(string s) {
        int ans =0;
        int ze = 0;
        int f = 1;
        for(int i=s.size()-1;i>=0;i--){
            if(s[i]=='1') {ans = ans + ze;f=1;}
            else if(s[i]=='0'){
                if(f==1) {
                    ze++;
                    f=0;
                }

            }
        }
        return ans;
    }
};
