class Solution {
public:
    int maxOperations(string s) {
        vector<int>temp;
        for(int i=0;i<s.size()-1;i++)
        {
            if(s[i]=='1' && s[i+1]=='0')
                temp.push_back(i+1);
        }
        int cnt = 0;
        for(int i=0;i<s.size()-1;i++)
        {
            if(s[i]=='1')
            {
                int k = lower_bound(temp.begin(),temp.end(),i)-temp.begin();
                cnt+=(temp.size()-k);
            }
        }
        return cnt;

    }
};
