class Solution {
public:
    int maxOperations(string s) {
        int OneCnt = 0;
        bool isPreZero = false;
        int res = 0;
        for(auto& c : s){
            if(c == '1'){
                OneCnt++;
                isPreZero = false;
            }else{
                if(isPreZero)
                    continue;
                isPreZero = true;
                res += OneCnt;
            }
        }
        return res;
    }
};
