class Solution {
    public int maxOperations(String s) {
        int c=0;

        if(s.length()==1)
        {
            return 0;
        }
        for(int i=0;i<s.length()-1;i++)
        {
            if(s.charAt(i)=='1'&&s.charAt(i+1)=='0')
            {
                c++;
            }
        }
        int ans=0;
        for(int i=0;i<s.length()-1;i++)
        {
            char ch=s.charAt(i);
            if(ch=='1'&&s.charAt(i+1)=='0')
            {
                ans+=c;
                c--;
            }
            else if(ch=='1')
            {
                ans+=c;
            }
        }
        return ans;

    }
}
