class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)

        lefts = [0] * n

        runner = 0
        out = 0

        for i in range(n):
            lefts[i] = runner
            if s[i] == '1':
                runner += 1
            if s[i] == '0' and (i+1 == n or s[i+1] == '1'):
                out += lefts[i]

        return out
